changeSet.sql:
CREATE TABLE testshop.lang ( langId INT NOT NULL AUTO_INCREMENT COMMENT "langId", code CHAR(2) NOT NULL COMMENT "ISO-639 language code", description VARCHAR(50) COMMENT "internal description of language", PRIMARY KEY (langId) ) COMMENT = "Lang contains available languages of the app";
CREATE TABLE testshop.client ( clientId INT NOT NULL AUTO_INCREMENT, PRIMARY KEY (clientId) );
CREATE TABLE testshop2.product ( productId BIGINT(20) NOT NULL AUTO_INCREMENT COMMENT "productId", clientId INT NOT NULL COMMENT "clientId", price DECIMAL(10, 2) NOT NULL COMMENT "price of the product in Euro", typeId VARCHAR(255) NOT NULL COMMENT "typeId, enumeration - one of: books,...", createdAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, updatedAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, deleted tinyint(1) NOT NULL DEFAULT 0 COMMENT "if this product is marked as deleted", PRIMARY KEY (productId), CONSTRAINT fk_product_clientId FOREIGN KEY (clientId) REFERENCES testshop.client (clientId) ON DELETE CASCADE ) COMMENT = "Client-specific Products";
CREATE TABLE testshop2.product_lang ( productId BIGINT(20) NOT NULL, langId INT NOT NULL, name VARCHAR(255) NOT NULL, description TEXT NOT NULL, PRIMARY KEY (productId, langId), CONSTRAINT fk_product_lang_productId FOREIGN KEY (productId) REFERENCES testshop2.product (productId) ON DELETE CASCADE, CONSTRAINT fk_product_lang_langId FOREIGN KEY (langId) REFERENCES testshop.lang (langId) ON DELETE CASCADE );

changeSet.rollback:
