changeSet.sql:
CREATE TABLE lang ( langId INT NOT NULL AUTO_INCREMENT, code CHAR(2) NOT NULL, description VARCHAR(50), PRIMARY KEY (langId) );
CREATE TABLE client ( clientId INT NOT NULL AUTO_INCREMENT, PRIMARY KEY (clientId) );
CREATE TABLE product ( productId BIGINT(20) NOT NULL AUTO_INCREMENT, clientId INT NOT NULL, price DECIMAL(10, 2) NOT NULL, typeId VARCHAR(255) NOT NULL, createdAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP, updatedAt DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP, deleted tinyint(1) NOT NULL DEFAULT 0, PRIMARY KEY (productId), CONSTRAINT fk_product_clientId FOREIGN KEY (clientId) REFERENCES client (clientId) ON DELETE CASCADE );
CREATE TABLE product_lang ( productId BIGINT(20) NOT NULL, langId INT NOT NULL, name VARCHAR(255) NOT NULL, description TEXT NOT NULL, PRIMARY KEY (productId, langId), CONSTRAINT fk_product_lang_productId FOREIGN KEY (productId) REFERENCES product (productId) ON DELETE CASCADE, CONSTRAINT fk_product_lang_langId FOREIGN KEY (langId) REFERENCES lang (langId) ON DELETE CASCADE );

changeSet.rollback:
DROP TABLE product_lang;
DROP TABLE product;
DROP TABLE client;
DROP TABLE lang;
